import React, { useState } from 'react';

const hairdressers = [
    { name: 'Anne', slots: ['9:00', '10:00', '11:00', '14:00', '15:00'] },
    { name: 'Ryan', slots: ['9:30', '10:30', '11:30', '14:30', '15:30'] },
    { name: 'Nathan', slots: ['9:00', '10:00', '11:00', '14:00', '15:00'] },
    { name: 'Coiffeur 4', slots: ['9:30', '10:30', '11:30', '14:30', '15:30'] },
    { name: 'Coiffeur 5', slots: ['9:00', '10:00', '11:00', '14:00', '15:00'] },
];

const Réservation = () => {
    const [selectedSlot, setSelectedSlot] = useState(null);

    const selectSlot = (hairdresser, slot) => {
        setSelectedSlot(`Vous avez réservé un créneau avec ${hairdresser} à ${slot}`);
    };

    return (
        <div className="p-10">
            <h1 className="text-4xl mb-10 text-white font-bold">Réservation de coiffeur</h1>
            {hairdressers.map((hairdresser, index) => (
                <div key={index} className="mb-5">
                    <h2 className="text-2xl mb-2 text-white font-bold shadow-2xl">{hairdresser.name}</h2>
                    {hairdresser.slots.map((slot, index) => (
                        <button
                            key={index}
                            className="mr-2 mb-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                            onClick={() => selectSlot(hairdresser.name, slot)}
                        >
                            {slot}
                        </button>
                    ))}
                </div>
            ))}
            {selectedSlot && <p className="mt-5 text-green-500">{selectedSlot}</p>}
        </div>
    );
};

export default Réservation;
